import React from 'react';
import "./PodcastLike.css"
import { addFav } from '../../services/Api';


export class Like extends React.Component {
    state = {
        favorites:' this.props.favorites.length'
      }

      handleClick = (event) => {

        addFav(this.props.likeId)
          .then(response => {
            this.setState({
                favorites: this.state.favorites + response.data.favorites
            })
          })
      }

    render() {
        const text = this.state.liked ? 'liked' : 'haven\'t liked';
        const label = this.state.liked ? 'Unlike' : 'Like'
        return (
            <div className="customContainer">
                <button className="btn btn-primary" onClick={this.handleClick}>
                    {label}</button>
            </div>
        );
    }
}

export default Like;
