import React, { useState } from 'react';
import {getPodcastsFromSpotify} from './services/Api'
import PodcastCard from './components/Podcast/PodcastCard';
import NavBar from './components/NavBar/NavBar'
import { Redirect, Route, Switch } from 'react-router-dom';

function App(){
    const [search, setSearch] = useState('')
    const [podcasts, setPodcast] = useState([])
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')))

  const loggedIn = (loggedUser) => {
    console.log("setting item")
    localStorage.setItem('user', JSON.stringify(loggedUser))
    setUser(loggedUser)
  }

  const loggedOut = () => {
    localStorage.removeItem('user')
    setUser(undefined)
  }

    const getPodcast = () => {

        getPodcastsFromSpotify({ search: search })
            .then((res) => {
                //pìntar los resultados

                setPodcast(res)

            })
            .catch(e => console.log(e))
    }

    return (
        <div>

        <NavBar user={user} logOut={loggedOut}/>
        <Switch>
        <Switch>
        {/* <AuthenticatorRouter exact path="/wines" component={WineList}/> */}
        <AuthenticatorRouter path="/wines" user={user} render={(props) => <WineList {...props} user={user} logOut={loggedOut} />} />
        {/* <AuthenticatorRouter path="/create" user={user} render={(props) => <WineForm {...props} user={user} logOut={loggedOut} />} /> */}

        {/* <AuthenticatorRouter path="/detail/:id" user={user} render={(props) => <ProductDetail {...props} user={user} logOut={loggedOut} />} /> */}
        <Route path="/login" render={(props) => <Login {...props} user={user} logIn={loggedIn} />} />
        <Route exact path="/register" component={UserForm} />
        <Route exact path="/create" component={WineForm} />
        <Route exact path="/my-wines" user={user} render={(props) => <MyWines {...props} user={user} logOut={loggedOut} />} />


        <Redirect to="/wines" />
      </Switch>
        </Switch>

        </div>
    );
};

export default App;
